package javaprelearn.exam05._1;

public class inc_decreaseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int iNum1=10, result;
result = ++iNum1;//연산 시작 전에 ++
System.out.println(result);
System.out.println(iNum1);
result = iNum1++;// 연산이 다 끝난 후에 ++
System.out.println(result);
System.out.println(iNum1);

	}

}
